def save_error_to_file(file_name):
    path_file = os.path.abspath(path + file_name)
    def dekor(function):
        def closed(*args, **kwargs):
            try:
                wynik = function(*args, **kwargs)
                return wynik
            except Exception as e:
                with open(path_file, 'a') as plik:
                    plik.write(f"Time: {datetime.datetime.now()}:\n")
                    plik.write(f"Error in function {function.__name__}:\n")
                    plik.write(f"Type Error: {type(e).__name__}\n")
                    plik.write(f"Error: {str(e)}\n")
                    plik.write("Stock:\n")
                    traceback.print_exc(file=plik)
                    plik.write("\n\n")
                print(f"Error saved in: {path_file}")
                traceback.print_exc()  
        return closed
    return dekor